import React from 'react'
import { useEffect, useState } from 'react'
import { useTheme } from 'next-themes'
import IconButton from '@material-ui/core/IconButton'
import WbSunnyIcon from '@material-ui/icons/WbSunny'
import Brightness2Icon from '@material-ui/icons/Brightness2'
import Head from 'next/head'
import Link from 'next/link'
import { useRouter } from 'next/router'
import styles from '../styles/layout.module.css'

export const siteTitle = 'Kelly Harrop, üé® üë©üèª‚Äçüíª'

export default function Layout({ children }: { children: React.ReactNode }) {
  const router = useRouter()
  const [isMounted, setIsMounted] = useState(false)
  const { theme, setTheme } = useTheme()
  useEffect(() => {
    setIsMounted(true)
  }, [])
  const switchTheme = () => {
    if (isMounted) {
      setTheme(theme === 'light' ? 'dark' : 'light')
    }
  }

  return (
    <div>
      <Head>
        <link rel="icon" href="/favicon.ico" />
        <meta name="viewport" content="width=device-width, initial-scale=1" />
        <meta name="description" content="Kelly Harrop, UX Engineer" />
        <title>Kelly Harrop, üé® üë©üèª‚Äçüíª</title>
        <meta name="og:title" content={siteTitle} />
        <meta name="twitter:card" content="summary_large_image" />
      </Head>
      <header className="inline-flex">
        <nav>
          <ul className={`inline-flex ${styles.nav}`}>
            <li>
              <Link href="/">
                <a>Kelly Harrop</a>
              </Link>
            </li>
            <li
              className={
                router.pathname == '/working-style' ? styles.active : ''
              }
            >
              <Link href="/working-style">
                <a>Working Style</a>
              </Link>
            </li>
            <li className={router.pathname == '/blog' ? styles.active : ''}>
              <Link href="/blog">
                <a>Blog</a>
              </Link>
            </li>
          </ul>
        </nav>
        <div className="media">
          <ul>
            <li>
              {isMounted && (
                <IconButton
                  size="small"
                  onClick={switchTheme}
                  title={`Activate ${
                    theme === 'light' ? 'dark' : 'light'
                  } mode`}
                  aria-label={`Activate ${
                    theme === 'light' ? 'dark' : 'light'
                  } mode`}
                >
                  {theme === 'light' ? <WbSunnyIcon /> : <Brightness2Icon />}
                </IconButton>
              )}
            </li>
          </ul>
        </div>
      </header>
      <main>{children}</main>
    </div>
  )
}
