import React from 'react'
import { useEffect, useState } from 'react'
import { useTheme } from 'next-themes'

import Head from 'next/head'
import Link from 'next/link'
import { useRouter } from 'next/router'
import styles from '../styles/layout.module.css'

export const siteTitle = 'Kelly Harrop, üé® üë©üèª‚Äçüíª'

export default function Layout({ children }: { children: React.ReactNode }) {
  const router = useRouter()
  const [isMounted, setIsMounted] = useState(false)
  const { theme, setTheme } = useTheme()
  useEffect(() => {
    setIsMounted(true)
  }, [])
  const switchTheme = () => {
    if (isMounted) {
      setTheme(theme === 'light' ? 'dark' : 'light')
    }
  }

  return (
    <div>
      <Head>
        <link rel="icon" href="/favicon.ico" />
        <meta name="viewport" content="width=device-width, initial-scale=1" />
        <meta name="description" content="Kelly Harrop, UX Engineer" />
        <title>Kelly Harrop, üé® üë©üèª‚Äçüíª</title>
        <meta name="og:title" content={siteTitle} />
        <meta name="twitter:card" content="summary_large_image" />
      </Head>
      <header className={styles.header}>
        <div className="content-wrapper">
          <nav>
            <ul className={styles.group}>
              <li>
                <Link href="/">
                  <a>Kelly Harrop</a>
                </Link>
              </li>
              <li
                className={
                  router.pathname == '/working-style' ? styles.active : ''
                }
              >
                <Link href="/working-style">
                  <a>Working Style</a>
                </Link>
              </li>
              <li className={router.pathname == '/blog' ? styles.active : ''}>
                <Link href="/blog">
                  <a>Blog</a>
                </Link>
              </li>
            </ul>
          </nav>
          <div className={styles.group}>
            <ul>
              <li>
                {isMounted && (
                  <button className="inline-block p-3 text-center text-white transition rounded-full shadow ripple hover:shadow-lg hover:bg-blue-800 focus:outline-none">
                    <svg
                      className="w-5 h-5 text-white"
                      xmlns="http://www.w3.org/2000/svg"
                      viewBox="0 0 20 20"
                      fill="currentColor"
                    >
                      <path
                        fill-rule="evenodd"
                        d="M18 10c0 3.866-3.582 7-8 7a8.841 8.841 0 01-4.083-.98L2 17l1.338-3.123C2.493 12.767 2 11.434 2 10c0-3.866 3.582-7 8-7s8 3.134 8 7zM7 9H5v2h2V9zm8 0h-2v2h2V9zM9 9h2v2H9V9z"
                        clip-rule="evenodd"
                      />
                    </svg>
                  </button>
                  // <IconButton
                  //   onClick={switchTheme}
                  //   title={`Activate ${
                  //     theme === 'light' ? 'dark' : 'light'
                  //   } mode`}
                  //   aria-label={`Activate ${
                  //     theme === 'light' ? 'dark' : 'light'
                  //   } mode`}
                  // >
                  //   {theme === 'light' ? (
                  //     <WbSunnyIcon fontSize="small" />
                  //   ) : (
                  //     <Brightness2Icon fontSize="small" />
                  //   )}
                  // </IconButton> */}
                )}
              </li>
            </ul>
          </div>
        </div>
      </header>
      <main>{children}</main>
    </div>
  )
}
