import Link from 'next/link'
import { PreviewdPost } from './previewPost'

export const Post = ({ post }) => {
  const {
    link,
    module: { meta }
  } = post

  return (
    <article>
      <PreviewPost meta={meta} />
      <Link href={'/blog' + link}>
        <a>Read more &rarr;</a>
      </Link>
      <style jsx>
        {`
          article {
            margin-bottom: 3rem;
          }
        `}
      </style>
    </article>
  )
}
