import Link from 'next/link'

export const PostPreview = ({ post }) => {
  const {
    link,
    module: { meta }
  } = post

  return (
    <article>
      <Link href={'/blog' + link}>
        <a className="post-preview flex">
          <div>
            <h2>{meta.title}</h2>
            <div className="description">{meta.description}</div>

            <div className="notes">
              <time>{meta.date}</time> â€¢ <span>{meta.readTime} min read</span>{' '}
            </div>
          </div>
          <img src={meta.image} />
        </a>
      </Link>
    </article>
  )
}
