import React from 'react'
import { useEffect, useState } from 'react'
import { useTheme } from 'next-themes'
import WbSunnyIcon from '../public/images/WbSunnyIcon.svg'
import Brightness2Icon from '../public/images/WbSunnyIcon.svg'
import Head from 'next/head'
import Link from 'next/link'
import { useRouter } from 'next/router'
import styles from '../styles/layout.module.css'

export const siteTitle = 'Kelly Harrop, ğŸ¨ ğŸ‘©ğŸ»â€ğŸ’»'

export default function Layout({ children }: { children: React.ReactNode }) {
  const router = useRouter()
  const [isMounted, setIsMounted] = useState(false)
  const { theme, setTheme } = useTheme()
  useEffect(() => {
    setIsMounted(true)
  }, [])
  const switchTheme = () => {
    if (isMounted) {
      setTheme(theme === 'light' ? 'dark' : 'light')
    }
  }

  return (
    <div>
      <Head>
        <link rel="icon" href="/favicon.ico" />
        <meta name="viewport" content="width=device-width, initial-scale=1" />
        <meta name="description" content="Kelly Harrop, UX Engineer" />
        <title>Kelly Harrop, ğŸ¨ ğŸ‘©ğŸ»â€ğŸ’»</title>
        <meta name="og:title" content={siteTitle} />
        <meta name="twitter:card" content="summary_large_image" />
      </Head>
      <header className={styles.header}>
        <div className="content-wrapper">
          <nav>
            <ul className={styles.group}>
              <li>
                <Link href="/">
                  <a>Kelly Harrop</a>
                </Link>
              </li>
              <li
                className={
                  router.pathname == '/working-style' ? styles.active : ''
                }
              >
                <Link href="/working-style">
                  <a>Working Style</a>
                </Link>
              </li>
              <li className={router.pathname == '/blog' ? styles.active : ''}>
                <Link href="/blog">
                  <a>Blog</a>
                </Link>
              </li>
            </ul>
          </nav>
          <div className={styles.group}>
            <ul>
              <li>
                {isMounted && (
                  <button
                    className="p-3 text-center transition rounded-full ripple hover:shadow-lg focus:outline-none dark:text-white"
                    onClick={switchTheme}
                    title={`Activate ${
                      theme === 'light' ? 'dark' : 'light'
                    } mode`}
                    aria-label={`Activate ${
                      theme === 'light' ? 'dark' : 'light'
                    } mode`}
                  >
                    {theme === 'light' ? <WbSunnyIcon /> : <Brightness2Icon />}
                  </button>
                )}
              </li>
            </ul>
          </div>
        </div>
      </header>
      <main>{children}</main>
    </div>
  )
}
