import Link from 'next/link'

export const PostPreview = ({ post }) => {
  const {
    link,
    module: { meta }
  } = post

  return (
    <article>
      <Link href={'/blog' + link}>
        <a className="post-preview">
          <h3>{meta.title}</h3>
          <div className="description">{meta.description}</div>
          <time>{meta.date}</time> â€¢{' '}
          <span className="read-time">{meta.readTime} min read</span>
        </a>
      </Link>
    </article>
  )
}
